// src/components/comment/CommentSection.jsx
import { useEffect, useState } from "react";
import Apis from "../configs/Apis";
import { Button, Form } from "react-bootstrap";

const CommentSection = ({ postId, locked }) => {
  const [comments, setComments] = useState([]);
  const [content, setContent] = useState("");

  useEffect(() => {
  const loadComments = () => {
    Apis.getCommentsByPostId(postId)
      .then(res => setComments(res.data))
      .catch(err => console.error("Lỗi load comment:", err));
  };

  loadComments();
}, [postId]);


  const handleSubmit = (e) => {
    e.preventDefault();
    if (!content.trim()) return;

    Apis.createComment({ postId, content })
      .then(() => {
        setContent("");
      })
      .catch(err => console.error("Lỗi tạo comment:", err));
  };

  return (
    <div>
      <h5>Bình luận</h5>

      {locked && <p className="text-danger">Bình luận đã bị khóa</p>}

      {!locked && (
        <Form onSubmit={handleSubmit} className="mb-3">
          <Form.Group>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Nhập bình luận..."
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </Form.Group>
          <Button type="submit" className="mt-2">Gửi</Button>
        </Form>
      )}

      <ul className="list-group">
        {comments.map(c => (
          <li key={c.id} className="list-group-item">
            <strong>{c.author?.fullName || "Ẩn danh"}:</strong> {c.content}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommentSection;
