import React, { useEffect, useState } from "react";
import { ListGroup, Spinner, Button } from "react-bootstrap";
import axios from "axios";
import { authApi, endpoints } from "../../configs/Api"; // sửa đúng path

const CommentList = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadComments = async () => {
      try {
        const res = await authApi().get(`${endpoints.comments}/post/${postId}`);
        setComments(res.data);
      } catch (err) {
        console.error("Lỗi load comments:", err);
      } finally {
        setLoading(false);
      }
    };

    if (postId) loadComments();
  }, [postId]);

  const renderReplies = (replies, depth = 1) => {
    return replies.map((reply) => (
      <ListGroup.Item key={reply.id} style={{ marginLeft: depth * 30, borderLeft: "2px solid #ccc" }}>
        <strong>{reply.user?.name || "Ẩn danh"}:</strong> {reply.content}
        {reply.replies && reply.replies.length > 0 && renderReplies(reply.replies, depth + 1)}
      </ListGroup.Item>
    ));
  };

  if (loading) return <Spinner animation="border" variant="primary" size="sm" />;

  return (
    <div className="mt-3">
      <h6>Bình luận</h6>
      <ListGroup variant="flush">
        {comments.length === 0 ? (
          <ListGroup.Item>Chưa có bình luận nào.</ListGroup.Item>
        ) : (
          comments.map((comment) => (
            <ListGroup.Item key={comment.id}>
              <strong>{comment.user?.name || "Ẩn danh"}:</strong> {comment.content}
              {comment.replies && comment.replies.length > 0 && renderReplies(comment.replies)}
            </ListGroup.Item>
          ))
        )}
      </ListGroup>
    </div>
  );
};

export default CommentList;
