import { useEffect, useState } from "react";
import { Alert, Button, Col, Form, Row } from "react-bootstrap";
import { endpoints } from "../configs/Apis";
import PostItem from "./PostItem";
import axios from "axios";
import MySpinner from "./layout/MySpinner";

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);

  // Hàm tải bài viết
  const loadPosts = async () => {
    try {
      let url = `${endpoints.posts}?page=${page}`;
      if (q) url += `&kw=${q}`;

      const res = await axios.get(url);

      if (res.data.length === 0 && page > 1) {
        setPage(0); // Không còn bài viết để tải
      } else {
        if (page === 1)
          setPosts(res.data);
        else
          setPosts(prev => [...prev, ...res.data]);
      }
    } catch (err) {
      console.error("Lỗi tải bài viết:", err);
    } finally {
      setLoading(false);
    }
  };

  // Load khi tìm kiếm hoặc phân trang
  useEffect(() => {
    setLoading(true);
    const timer = setTimeout(() => {
      if (page > 0) loadPosts();
    }, 500); // debounce tìm kiếm

    return () => clearTimeout(timer);
  }, [q, page]);

  // Reset về trang 1 khi từ khóa tìm kiếm thay đổi
  useEffect(() => {
    setPage(1);
  }, [q]);

  // Tải thêm bài viết
  const loadMore = () => {
    setPage(prev => prev + 1);
  };

  return (
    <>
      <Form className="mb-3 mt-2">
        <Form.Control
          type="text"
          placeholder="Tìm bài viết..."
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
      </Form>

      {loading && <MySpinner />}

      {!loading && posts.length === 0 && (
        <Alert variant="info">Không có bài viết nào!</Alert>
      )}

      <Row>
        {posts.map(post => (
          <Col key={post.id} md={6} className="mb-3">
            <PostItem post={post} />
          </Col>
        ))}
      </Row>

      {!loading && page > 0 && (
        <div className="text-center mb-3">
          <Button onClick={loadMore}>Xem thêm</Button>
        </div>
      )}
    </>
  );
};

export default PostList;
