import { useEffect, useState } from "react";
import { Alert, Button, Card, Col, Form, Row } from "react-bootstrap";
import { endpoints } from "./configs/Apis";

import PostItem from "./PostItem";

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);

  const loadPosts = async () => {
    try {
      let url = `${endpoints.posts}?page=${page}`;
      if (q) url += `&kw=${q}`;

      const res = await axios.get(url);
      if (res.data.length === 0 && page > 1)
        setPage(0);
      else {
        setPosts(prev =>
          page === 1 ? res.data : [...prev, ...res.data]
        );
      }
    } catch (err) {
      console.error("Lỗi tải bài viết:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    setLoading(true);
    const timer = setTimeout(() => {
      if (page > 0) loadPosts();
    }, 400);
    return () => clearTimeout(timer);
  }, [q, page]);

  useEffect(() => {
    setPage(1);
  }, [q]);

  return (
    <>
      <Form className="mb-3 mt-2">
        <Form.Control
          placeholder="Tìm bài viết..."
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
      </Form>

      {posts.length === 0 && !loading && (
        <Alert variant="info">Không có bài viết nào!</Alert>
      )}

      <Row>
        {posts.map(post => (
          <Col key={post.id} md={6} className="mb-3">
            <PostItem post={post} />
          </Col>
        ))}
      </Row>

      {page > 0 && (
        <div className="text-center mb-3">
          <Button onClick={() => setPage(p => p + 1)}>Xem thêm</Button>
        </div>
      )}
    </>
  );
};

export default PostList;
