import { useEffect, useState } from "react";
import axiosClient, { endpoints } from "../configs/axiosClient";

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const res = await axiosClient.get(endpoints.posts); // gọi: http://localhost:8080/AlumniSocialNetwork/api/post
        console.log("Posts:", res.data);
        setPosts(res.data);
      } catch (err) {
        console.error("Lỗi khi tải bài viết:", err);
      }
    };

    loadPosts();
  }, []);

  return (
    <>
      <h2>Bài viết</h2>
      {posts.map(p => (
        <div key={p.id}>
          <strong>{p.user.fullName}</strong>: {p.content}
        </div>
      ))}
    </>
  );
};

export default PostList;
