import { useState } from "react";
import { Button, Image } from "react-bootstrap";

const CommentList = ({ postId, comments }) => {
  const [expandedComments, setExpandedComments] = useState({});

  const toggleReplies = (commentId) => {
    setExpandedComments(prev => ({
      ...prev,
      [commentId]: !prev[commentId]
    }));
  };

  const renderReplies = (comment) => {
    if (!comment.replies || comment.replies.length === 0) return null;
    if (!expandedComments[comment.id]) {
      return (
        <Button
          variant="link"
          size="sm"
          className="ps-5"
          onClick={() => toggleReplies(comment.id)}
        >
          Xem tất cả {comment.replies.length} phản hồi
        </Button>
      );
    }

    return comment.replies.map(reply => (
      <div key={reply.id} className="d-flex ps-5 pt-2">
        <Image src={reply.user.avatar} roundedCircle width={30} height={30} className="me-2" />
        <div>
          <strong>{reply.user.name}</strong>
          <div>{reply.content}</div>
          <div className="text-muted" style={{ fontSize: "0.75rem" }}>
            {new Date(reply.createdDate).toLocaleString()}
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className="pt-3">
      {comments.map(comment => (
        <div key={comment.id} className="d-flex pt-2">
          <Image src={comment.user.avatar} roundedCircle width={35} height={35} className="me-2" />
          <div>
            <strong>{comment.user.name}</strong>
            <div>{comment.content}</div>
            <div className="text-muted" style={{ fontSize: "0.75rem" }}>
              {new Date(comment.createdDate).toLocaleString()}
            </div>
            {/* Phản hồi */}
            {renderReplies(comment)}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CommentList;
