import { Card } from "react-bootstrap";

const PostItem = ({ post }) => {
  return (
    <Card>
      <Card.Body>
        <Card.Title>{post.title}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          {post.creator?.name || "Ẩn danh"} - {new Date(post.createdAt).toLocaleString()}
        </Card.Subtitle>
        <Card.Text>{post.content}</Card.Text>
        {/* Có thể bổ sung nút Like/Comment ở đây */}
      </Card.Body>
    </Card>
  );
};

export default PostItem;
