import { useState, useEffect, useCallback } from "react";
import {
  Card,
  Row,
  Col,
  Button,
  Image,
  OverlayTrigger,
  Popover,
} from "react-bootstrap";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";
import { authApi, endpoints } from "../configs/Apis";

const REACTION_TYPES = [
  { type: "LIKE", emoji: "👍", label: "Thích" },
  { type: "HAHA", emoji: "😄", label: "Haha" },
  { type: "LOVE", emoji: "❤️", label: "Yêu thích" },
];

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [reactions, setReactions] = useState([]);
  const [userReaction, setUserReaction] = useState(null);

  // Giả định lấy userId từ localStorage hoặc context
  const currentUserId = localStorage.getItem("userId");

  const loadComments = useCallback(async () => {
    try {
      const res = await authApi().get(`${endpoints.comments}/post/${post.id}`);
      setComments(res.data);
    } catch (err) {
      console.error("Load comments failed:", err);
    }
  }, [post.id]);

  const loadReactions = useCallback(async () => {
    try {
      const res = await authApi().get(
        `${endpoints.reactions}/stats/${post.id}`
      );
      const stats = Object.entries(res.data).map(
        ([reactionType, count]) => ({
          reactionType,
          count,
        })
      );
      setReactions(stats);
    } catch (err) {
      console.error("Load reactions failed:", err);
    }
  }, [post.id]);

  const loadUserReaction = useCallback(async () => {
    try {
      const res = await authApi().get(
        `${endpoints.reactions}/user/${currentUserId}/post/${post.id}`
      );
      setUserReaction(res.data.reactionType);
    } catch (err) {
      setUserReaction(null); // Không có reaction trước đó
    }
  }, [post.id, currentUserId]);

  const sendReaction = async (reactionType) => {
    try {
      setUserReaction(reactionType); // cập nhật UI ngay
      const payload = { postId: post.id, reactionType };
      await authApi().post(endpoints.reactions, payload);
      loadReactions(); // cập nhật lại thống kê
    } catch (err) {
      console.error("Send reaction failed:", err);
    }
  };

  useEffect(() => {
    loadComments();
    loadReactions();
    loadUserReaction();
  }, [loadComments, loadReactions, loadUserReaction]);

  const reactionPopover = (
    <Popover>
      <Popover.Body className="d-flex gap-2">
        {REACTION_TYPES.map((r) => (
          <Button
            key={r.type}
            variant="light"
            className="rounded-circle"
            onClick={() => sendReaction(r.type)}
            title={r.label}
          >
            <span style={{ fontSize: "1.3rem" }}>{r.emoji}</span>
          </Button>
        ))}
      </Popover.Body>
    </Popover>
  );

  const currentReaction =
    REACTION_TYPES.find((r) => r.type === userReaction) || REACTION_TYPES[0];

  return (
    <Card className="mb-4 shadow-sm rounded-4 p-3">
      {/* Header */}
      <Row className="mb-2 align-items-center">
        <Col xs="auto">
          <Image src={post.user.avatar} roundedCircle width={40} height={40} />
        </Col>
        <Col>
          <strong>{post.user.name}</strong>
          <div className="text-muted" style={{ fontSize: "0.85rem" }}>
            {new Date(post.createdDate).toLocaleString()}
          </div>
        </Col>
      </Row>

      {/* Content */}
      <p>{post.content}</p>
      {post.image && (
        <img src={post.image} alt="post" className="img-fluid rounded-3" />
      )}

      {/* Reaction summary */}
      <ReactionStats stats={reactions} />

      {/* Action buttons */}
      <Row className="text-center border-top border-bottom py-2 mt-2">
        <Col>
          <OverlayTrigger
            trigger="click"
            placement="top"
            overlay={reactionPopover}
            rootClose
          >
            <Button
              variant="link"
              size="sm"
              className={`fw-semibold ${
                userReaction ? "text-primary" : "text-secondary"
              }`}
            >
              <span style={{ fontSize: "1rem" }}>
                {currentReaction.emoji}
              </span>{" "}
              {currentReaction.label}
            </Button>
          </OverlayTrigger>
        </Col>
        <Col>
          <Button
            variant="link"
            size="sm"
            className="text-secondary fw-semibold"
          >
            💬 Bình luận
          </Button>
        </Col>
        <Col>
          <Button
            variant="link"
            size="sm"
            className="text-secondary fw-semibold"
          >
            ↗️ Chia sẻ
          </Button>
        </Col>
      </Row>

      {/* Comment List */}
      <CommentList postId={post.id} comments={comments} />
    </Card>
  );
};

export default PostItem;
