const ReactionStats = ({ reactions, emojis }) => {
  return (
    <div className="flex items-center space-x-4">
      {reactions.map(({ reactionType, count }) => (
        count > 0 && (
          <div key={reactionType} className="flex items-center space-x-1">
            <span className="text-lg">{emojis[reactionType]}</span>
            <span className="text-sm text-gray-600">{count}</span>
          </div>
        )
      ))}
    </div>
  );
};

export default ReactionStats;
