// src/configs/Apis.js
import axios from "axios";
import cookie from "react-cookies";

// ✅ Base URL backend
export const BASE_URL = "http://localhost:8080/AlumniSocialNetwork/api";

// ✅ Các endpoint rõ ràng
export const endpoints = {
  posts: `${BASE_URL}/post`,
  comments: `${BASE_URL}/comment`,
  reactions: `${BASE_URL}/reaction`,
};

// ✅ Axios instance không auth
export default axios.create({
  baseURL: BASE_URL,
});

// ✅ Axios instance có auth token từ cookie
export const authApi = () =>
  axios.create({
    baseURL: BASE_URL,
    headers: {
      Authorization: `Bearer ${cookie.load("token")}`,
    },
  });
