import { useState, useEffect, useCallback } from "react";
import { Card, Row, Col, Button, Image } from "react-bootstrap";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";
import { authApi, endpoints } from "../configs/Apis";

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [reactions, setReactions] = useState([]);

  const loadComments = useCallback(async () => {
    try {
      const res = await authApi().get(`${endpoints.comments}/post/${post.id}`);
      setComments(res.data);
    } catch (err) {
      console.error("Load comments failed:", err);
    }
  }, [post.id]);

  const loadReactions = useCallback(async () => {
    try {
      const res = await authApi().get(`${endpoints.reactions}/stats/${post.id}`);
      const stats = Object.entries(res.data).map(([reactionType, count]) => ({
        reactionType, count,
      }));
      setReactions(stats);
    } catch (err) {
      console.error("Load reactions failed:", err);
    }
  }, [post.id]);

  useEffect(() => {
    loadComments();
    loadReactions();
  }, [loadComments, loadReactions]);

  // Các hàm xử lý nút hành động
  const handleLike = () => {
    alert(`Đã thích bài viết ${post.id}`);
    // TODO: Gọi API thích
  };

  const handleComment = () => {
    const commentBox = document.getElementById(`comment-box-${post.id}`);
    if (commentBox) commentBox.scrollIntoView({ behavior: "smooth" });
  };

  const handleShare = () => {
    const url = `${window.location.origin}/posts/${post.id}`;
    navigator.clipboard.writeText(url);
    alert("Đã sao chép liên kết bài viết!");
  };

  return (
    <Card className="mb-4 shadow-sm rounded-4 p-3 bg-white">
      {/* Header */}
      <Row className="mb-2 align-items-center">
        <Col xs="auto">
          <Image src={post.user.avatar} roundedCircle width={42} height={42} />
        </Col>
        <Col>
          <strong>{post.user.name}</strong>
          <div className="text-muted" style={{ fontSize: "0.85rem" }}>
            {new Date(post.createdDate).toLocaleString()}
          </div>
        </Col>
      </Row>

      {/* Content */}
      <div className="mb-2" style={{ fontSize: "1rem" }}>{post.content}</div>
      {post.image && (
        <img
          src={post.image}
          alt="post"
          className="img-fluid rounded-3 shadow-sm mb-2"
          style={{ maxHeight: "400px", objectFit: "cover" }}
        />
      )}

      {/* Reactions summary */}
      <ReactionStats stats={reactions} />

      {/* Action buttons */}
      <Row className="text-center border-top border-bottom py-2 mt-2">
        <Col>
          <Button variant="link" size="sm" className="text-decoration-none text-primary" onClick={handleLike}>
            👍 Thích
          </Button>
        </Col>
        <Col>
          <Button variant="link" size="sm" className="text-decoration-none text-primary" onClick={handleComment}>
            💬 Bình luận
          </Button>
        </Col>
        <Col>
          <Button variant="link" size="sm" className="text-decoration-none text-primary" onClick={handleShare}>
            ↗️ Chia sẻ
          </Button>
        </Col>
      </Row>

      {/* Comments */}
      <div id={`comment-box-${post.id}`}>
        <CommentList postId={post.id} comments={comments} />
      </div>
    </Card>
  );
};

export default PostItem;
