import { useEffect, useState } from "react";
import { Card, Button } from "react-bootstrap";
import axios, { endpoints } from "../configs/Apis";
import CommentList from "./CommentList";

const PostItem = ({ post }) => {
  const [reactions, setReactions] = useState({});
  const [showComments, setShowComments] = useState(false);

  // ‚úÖ T·∫£i reactions
  useEffect(() => {
    const fetchReactions = async () => {
      try {
        const res = await axios.get(endpoints.reactionStats(post.id));
        setReactions(res.data);
      } catch (err) {
        console.error("‚ùå Kh√¥ng t·∫£i ƒë∆∞·ª£c reactions:", err);
      }
    };

    fetchReactions();
  }, [post.id]);

  return (
    <Card className="shadow-sm mb-3">
      <Card.Body>
        <div className="d-flex align-items-center mb-2">
          <img src={post.user?.avatar} width={40} height={40} className="me-2 rounded-circle" />
          <div>
            <strong>{post.user?.name}</strong>
            <div className="text-muted" style={{ fontSize: "0.8rem" }}>
              {new Date(post.createdAt).toLocaleString()}
            </div>
          </div>
        </div>

        <Card.Text>{post.content}</Card.Text>
        {post.image && <img src={post.image} className="img-fluid rounded" alt="post" />}

        <div className="mt-2 text-muted">
          üëç {reactions.like || 0} ¬∑ ‚ù§Ô∏è {reactions.heart || 0} ¬∑ üòÜ {reactions.haha || 0}
        </div>

        <div className="d-flex justify-content-between mt-2">
          <Button variant="outline-primary" size="sm">Like</Button>
          <Button variant="outline-secondary" size="sm" onClick={() => setShowComments(!showComments)}>
            Comment
          </Button>
        </div>

        {showComments && <CommentList postId={post.id} />}
      </Card.Body>
    </Card>
  );
};

export default PostItem;
