// src/components/PostItem.jsx
import { useEffect, useState, useCallback } from "react";
import { Card, Button, Row, Col } from "react-bootstrap";
import { authApi, endpoints } from "../configs/Apis";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [reactions, setReactions] = useState([]);

  // ✅ Load comment
const loadComments = useCallback(async () => {
  try {
    const res = await authApi().get(`${endpoints.comments}/post/${post.id}`);
    setComments(res.data);
  } catch (err) {
    console.error("Lỗi load comments", err);
  }
}, [post.id]);


  // ✅ Load reaction stats
  const loadReactions = useCallback(async () => {
  try {
    const res = await authApi().get(`${endpoints.reactions}/stats/${post.id}`);

    // Chuyển Map<String, Integer> thành mảng object [{reactionType, count}]
    const statsArray = Object.entries(res.data).map(([reactionType, count]) => ({
      reactionType,
      count
    }));

    setReactions(statsArray);
  } catch (err) {
    console.error("Lỗi load reactions", err);
  }
}, [post.id]);


  useEffect(() => {
    loadComments();
    loadReactions();
  }, [loadComments, loadReactions]);

  return (
    <Card className="mb-3 shadow-sm rounded-4">
      <Card.Body>
        {/* Header: Avatar + name + createdDate */}
        <Row className="align-items-center mb-2">
          <Col xs={1}>
            <img src={post.user.avatar} alt="avatar" className="rounded-circle" width={40} height={40} />
          </Col>
          <Col>
            <strong>{post.user.name}</strong>
            <div className="text-muted" style={{ fontSize: "0.8rem" }}>
              {new Date(post.createdDate).toLocaleString()}
            </div>
          </Col>
        </Row>

        {/* Content */}
        <Card.Text>{post.content}</Card.Text>

        {/* Images (nếu có) */}
        {post.image && (
          <div className="mb-2">
            <img src={post.image} alt="post" className="img-fluid rounded-3" />
          </div>
        )}

        {/* Reaction stats */}
        <ReactionStats stats={reactions} />

        {/* Buttons: Like, Comment */}
        <Row className="mt-2">
          <Col>
            <Button variant="outline-primary" size="sm">👍 Like</Button>{" "}
            <Button variant="outline-danger" size="sm">❤️ Heart</Button>{" "}
            <Button variant="outline-warning" size="sm">😄 Haha</Button>{" "}
            <Button variant="outline-secondary" size="sm">💬 Comment</Button>
          </Col>
        </Row>

        {/* Comment list */}
        <hr />
        <CommentList  postId={post.id} />
      </Card.Body>
    </Card>
  );
};

export default PostItem;
