import { useEffect, useState } from "react";
import { Form, Button, Spinner } from "react-bootstrap";
import axios from "axios";
import { endpoints } from "../configs/Apis";

const CommentList = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [content, setContent] = useState("");

  const loadComments = async () => {
    try {
      const res = await axios.get(`${endpoints.comments}/post/${postId}`);
      setComments(res.data);
    } catch (err) {
      console.error("Error loading comments:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(endpoints.comments, {
        post: { id: postId },
        content,
      });
      setContent("");
      loadComments(); // reload after posting
    } catch (err) {
      console.error("Failed to submit comment:", err);
    }
  };

  useEffect(() => {
    loadComments();
  }, [postId]);

  return (
    <div className="mt-3 border-top pt-2">
      {loading ? (
        <Spinner animation="border" size="sm" />
      ) : (
        <>
          {comments.map(c => (
            <div key={c.id} className="mb-2">
              <strong>{c.user.name}</strong>: {c.content}
            </div>
          ))}

          <Form onSubmit={handleSubmit} className="d-flex mt-2">
            <Form.Control
              value={content}
              onChange={e => setContent(e.target.value)}
              placeholder="Viết bình luận..."
            />
            <Button type="submit" variant="primary" className="ms-2">Gửi</Button>
          </Form>
        </>
      )}
    </div>
  );
};

export default CommentList;
