import { useEffect, useState, useCallback } from "react";
import { Card, Button, Row, Col } from "react-bootstrap";
import { authApi, endpoints } from "../configs/Apis";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";

const PostItem = ({ post }) => {
  const [reactions, setReactions] = useState([]);
  const [showComments, setShowComments] = useState(false);

  const loadReactions = useCallback(async () => {
    try {
      const res = await authApi().get(endpoints.reactionStats(post.id));
      const statsArray = Object.entries(res.data).map(([reactionType, count]) => ({
        reactionType,
        count
      }));
      setReactions(statsArray);
    } catch (err) {
      console.error("L·ªói load reactions", err);
    }
  }, [post.id]);

  useEffect(() => {
    loadReactions();
  }, [loadReactions]);

  return (
    <Card className="mb-3 shadow-sm rounded-4">
      <Card.Body>
        {/* Header */}
        <Row className="align-items-center mb-2">
          <Col xs={1}>
            <img
              src={post.user.avatar}
              alt="avatar"
              className="rounded-circle"
              width={40}
              height={40}
            />
          </Col>
          <Col>
            <strong>{post.user.name}</strong>
            <div className="text-muted" style={{ fontSize: "0.8rem" }}>
              {new Date(post.createdDate).toLocaleString()}
            </div>
          </Col>
        </Row>

        {/* Content */}
        <Card.Text>{post.content}</Card.Text>

        {/* Images */}
        {post.image && (
          <div className="mb-2">
            <img src={post.image} alt="post" className="img-fluid rounded-3" />
          </div>
        )}

        {/* Reaction Stats */}
        <ReactionStats stats={reactions} />

        {/* Buttons */}
        <Row className="mt-2">
          <Col>
            <Button variant="outline-primary" size="sm">üëç Like</Button>{" "}
            <Button variant="outline-danger" size="sm">‚ù§Ô∏è Heart</Button>{" "}
            <Button variant="outline-warning" size="sm">üòÑ Haha</Button>{" "}
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={() => setShowComments(prev => !prev)}
            >
              {showComments ? "·∫®n b√¨nh lu·∫≠n" : "Xem b√¨nh lu·∫≠n"}
            </Button>
          </Col>
        </Row>

        {/* Comment Section */}
        {showComments && (
          <>
            <hr />
            <CommentList postId={post.id} />
          </>
        )}
      </Card.Body>
    </Card>
  );
};

export default PostItem;
