import { useEffect, useState } from "react";
import { Card, Button, Form, Collapse, Image, Spinner } from "react-bootstrap";
import axios from "axios";
import { endpoints } from "../configs/Apis";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";
import moment from "moment";

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [reactions, setReactions] = useState({});
  const [commentContent, setCommentContent] = useState("");
  const [showComments, setShowComments] = useState(false);
  const [loadingComments, setLoadingComments] = useState(false);

  // Load comment list
  const loadComments = async () => {
    setLoadingComments(true);
    try {
      const res = await axios.get(`${endpoints.comments}/post/${post.id}`);
      setComments(res.data);
    } catch (err) {
      console.error("Lỗi tải bình luận:", err);
    } finally {
      setLoadingComments(false);
    }
  };

  // Load reactions stats
  const loadReactions = async () => {
    try {
      const res = await axios.get(`${endpoints.reactions}/stats/${post.id}`);
      setReactions(res.data);
    } catch (err) {
      console.error("Lỗi tải cảm xúc:", err);
    }
  };

  // Toggle comment area
  const toggleComments = () => {
    setShowComments(!showComments);
    if (!showComments) loadComments();
  };

  useEffect(() => {
    loadReactions();
  }, [post.id]);

  return (
    <Card className="shadow-sm">
      <Card.Body>
        <div className="d-flex align-items-center mb-2">
          <Image src={post.user.avatar} roundedCircle width={40} height={40} className="me-2" />
          <div>
            <strong>{post.user.name}</strong><br />
            <small className="text-muted">{moment(post.createdDate).fromNow()}</small>
          </div>
        </div>
        <Card.Text>{post.content}</Card.Text>
        {post.image && <Card.Img src={post.image} className="mt-2 mb-2" />}

        {/* Reaction Stats */}
        <ReactionStats reactions={reactions} />

        <div className="d-flex justify-content-between mt-2">
          <Button variant="outline-primary" size="sm">👍 Thích</Button>
          <Button variant="outline-success" size="sm">❤️ Yêu</Button>
          <Button variant="outline-warning" size="sm">😄 Haha</Button>
          <Button variant="link" size="sm" onClick={toggleComments}>
            {showComments ? "Ẩn bình luận" : "Hiển thị bình luận"}
          </Button>
        </div>

        {/* Comment section */}
        <Collapse in={showComments}>
          <div>
            {loadingComments ? (
              <div className="mt-2 text-center"><Spinner animation="border" size="sm" /></div>
            ) : (
              <CommentList comments={comments} />
            )}

            <Form className="mt-2">
              <Form.Control
                as="textarea"
                rows={2}
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
                placeholder="Viết bình luận..."
              />
              <Button variant="primary" size="sm" className="mt-1">Gửi</Button>
            </Form>
          </div>
        </Collapse>
      </Card.Body>
    </Card>
  );
};

export default PostItem;
