// components/PostItem.js
import React, { useState } from "react";
import { Card, Button, Form, Dropdown } from "react-bootstrap";

const PostItem = ({ post, currentUser }) => {
    const [comments, setComments] = useState(post.comments || []);
    const [newComment, setNewComment] = useState("");

    const handleAddComment = () => {
        if (!newComment.trim()) return;
        setComments([...comments, {
            id: Date.now(),
            content: newComment,
            user: currentUser
        }]);
        setNewComment("");
    };

    const canDeleteComment = (comment) => {
        return comment.user.id === currentUser.id || post.user.id === currentUser.id;
    };

    const canEditComment = (comment) => comment.user.id === currentUser.id;

    return (
        <Card className="mb-3 shadow-sm">
            <Card.Body>
                <Card.Title>{post.user.fullName}</Card.Title>
                <Card.Text>{post.content}</Card.Text>

                {/* Reactions */}
                <div className="d-flex gap-2 mb-2">
                    <Button size="sm" variant="outline-primary">üëç Like</Button>
                    <Button size="sm" variant="outline-warning">üòÜ Haha</Button>
                    <Button size="sm" variant="outline-danger">‚ù§Ô∏è Love</Button>
                </div>

                {/* Comment input */}
                {!post.commentsLocked && (
                    <div className="mb-2">
                        <Form.Control
                            value={newComment}
                            onChange={e => setNewComment(e.target.value)}
                            placeholder="Vi·∫øt b√¨nh lu·∫≠n..."
                        />
                        <Button size="sm" className="mt-2" onClick={handleAddComment}>G·ª≠i</Button>
                    </div>
                )}

                {/* Comment list */}
                <div className="mt-3">
                    {comments.map((cmt, idx) => (
                        <div key={idx} className="border rounded p-2 mb-2 bg-light">
                            <strong>{cmt.user.fullName}</strong>: {cmt.content}
                            <div className="text-end">
                                {canEditComment(cmt) && <Button size="sm" variant="outline-info" className="me-1">S·ª≠a</Button>}
                                {canDeleteComment(cmt) && <Button size="sm" variant="outline-danger">X√≥a</Button>}
                            </div>
                        </div>
                    ))}
                </div>
            </Card.Body>
        </Card>
    );
};

export default PostItem;
