import { useState, useEffect, useCallback } from "react";
import {
  Card,
  Row,
  Col,
  Button,
  Image,
  OverlayTrigger,
  Popover,
} from "react-bootstrap";
import CommentList from "./CommentList";
import ReactionStats from "./ReactionStats";
import axios, { endpoints } from "../configs/Apis"; // Import default Axios instance

// T·∫°m hard-code user ID
const TEMP_USER_ID = 1;

const REACTION_TYPES = [
  { type: "LIKE", emoji: "üëç", label: "Th√≠ch" },
  { type: "HAHA", emoji: "üòÑ", label: "Haha" },
  { type: "LOVE", emoji: "‚ù§Ô∏è", label: "Y√™u th√≠ch" },
];

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [reactions, setReactions] = useState([]);

  const loadComments = useCallback(async () => {
    try {
      const url = `${endpoints.commentByPost(post.id)}`;
      console.log("Fetching comments:", url);
      const res = await axios.get(url);
      setComments(res.data || []);
    } catch (err) {
      console.error("Load comments failed:", err.response?.data || err.message);
      setComments([]);
    }
  }, [post.id]);

  const loadReactions = useCallback(async () => {
    try {
      const url = `${endpoints.reactionStats(post.id)}`;
      console.log("Fetching reactions:", url);
      const res = await axios.get(url);
      const stats = res.data
        ? Object.entries(res.data).map(([reactionType, count]) => ({
            reactionType,
            count,
          }))
        : [];
      setReactions(stats);
    } catch (err) {
      console.error("Load reactions failed:", err.response?.data || err.message);
      setReactions([]);
    }
  }, [post.id]);

 const sendReaction = async (reactionType) => {
  try {
    const payload = {
      type: reactionType, // S·ª≠a th√†nh "type"
      postId: { id: post.id }, // G·ª≠i object Post
      userId: { id: TEMP_USER_ID }, // G·ª≠i object User
    };
    console.log("Sending reaction payload:", payload);
    const response = await axios.post(endpoints.reactions, payload);
    console.log("Reaction response:", response.data);
    await loadReactions();
  } catch (err) {
    console.error("Send reaction failed:", {
      message: err.message,
      status: err.response?.status,
      data: err.response?.data,
    });
  }
};
  useEffect(() => {
    loadComments();
    loadReactions();
  }, [loadComments, loadReactions]);

  const reactionPopover = (
    <Popover>
      <Popover.Body className="d-flex justify-content-center gap-3 px-3">
        {REACTION_TYPES.map((r) => (
          <Button
            key={r.type}
            variant="light"
            className="rounded-circle shadow-sm border-0"
            onClick={() => sendReaction(r.type)}
            title={r.label}
            style={{
              width: "45px",
              height: "45px",
              fontSize: "1.5rem",
              padding: 0,
              lineHeight: "45px",
            }}
          >
            {r.emoji}
          </Button>
        ))}
      </Popover.Body>
    </Popover>
  );

  return (
    <Card className="mb-4 shadow-sm rounded-4 p-3">
      <Row className="mb-2 align-items-center">
        <Col xs="auto">
          <Image
            src={post.user.avatar}
            roundedCircle
            width={44}
            height={44}
            style={{ objectFit: "cover" }}
          />
        </Col>
        <Col>
          <strong>{post.user.name}</strong>
          <div className="text-muted" style={{ fontSize: "0.85rem" }}>
            {new Date(post.createdDate).toLocaleString()}
          </div>
        </Col>
      </Row>
      <p className="mb-2">{post.content}</p>
      {post.image && (
        <img
          src={post.image}
          alt="post"
          className="img-fluid rounded-3 mb-2"
        />
      )}
      <ReactionStats stats={reactions} />
      <Row className="text-center border-top border-bottom py-2 mt-2">
        <Col>
          <OverlayTrigger
            trigger="click"
            placement="top"
            overlay={reactionPopover}
            rootClose
          >
            <Button
              variant="link"
              size="sm"
              className="text-primary fw-semibold px-1"
            >
              üëç Th√≠ch
            </Button>
          </OverlayTrigger>
        </Col>
        <Col>
          <Button
            variant="link"
            size="sm"
            className="text-secondary fw-semibold px-1"
          >
            üí¨ B√¨nh lu·∫≠n
          </Button>
        </Col>
        <Col>
          <Button
            variant="link"
            size="sm"
            className="text-secondary fw-semibold px-1"
          >
            ‚ÜóÔ∏è Chia s·∫ª
          </Button>
        </Col>
      </Row>
      <CommentList postId={post.id} comments={comments} />
    </Card>
  );
};

export default PostItem;
