import React, { useEffect, useState } from "react";
import { ListGroup, Spinner } from "react-bootstrap";
import Apis, { endpoints } from "../configs/Apis";

const CommentList = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log("üìù CommentList nh·∫≠n ƒë∆∞·ª£c postId:", postId);

    if (!postId) {
      setLoading(false);
      return;
    }

    const loadComments = async () => {
      try {
        const res = await Apis.get(endpoints.commentByPost(postId));
        console.log("‚úÖ D·ªØ li·ªáu comment:", res.data);
        setComments(res.data);
      } catch (err) {
        console.error("‚ùå L·ªói load comments:", err);
      } finally {
        setLoading(false);
      }
    };

    loadComments();
  }, [postId]);

  const renderReplies = (replies, depth = 1) => {
    return replies.map((reply) => (
      <ListGroup.Item
        key={reply.id}
        style={{
          marginLeft: depth * 30,
          borderLeft: "2px solid #ccc",
          backgroundColor: "#f9f9f9",
        }}
      >
        <strong>{reply.user?.name || "·∫®n danh"}:</strong> {reply.content}
        {reply.replies && reply.replies.length > 0 && renderReplies(reply.replies, depth + 1)}
      </ListGroup.Item>
    ));
  };

  if (loading)
    return (
      <div className="text-center my-2">
        <Spinner animation="border" variant="primary" size="sm" />
      </div>
    );

  return (
    <div className="mt-3">
      <h6>B√¨nh lu·∫≠n</h6>
      <ListGroup variant="flush">
        {comments.length === 0 ? (
          <ListGroup.Item>Ch∆∞a c√≥ b√¨nh lu·∫≠n n√†o ho·∫∑c ƒë√£ x·∫£y ra l·ªói.</ListGroup.Item>
        ) : (
          comments.map((comment) => (
            <ListGroup.Item key={comment.id}>
              <strong>{comment.user?.name || "·∫®n danh"}:</strong> {comment.content}
              {comment.replies && comment.replies.length > 0 && renderReplies(comment.replies)}
            </ListGroup.Item>
          ))
        )}
      </ListGroup>
    </div>
  );
};

export default CommentList;
