// pages/Home.js
import React, { useEffect, useState } from "react";
import PostItem from "../components/PostItem";

const dummyUser = { id: 1, fullName: "Nguyễn Văn A" }; // currentUser giả lập

const dummyPosts = [
    {
        id: 1,
        content: "Chào mừng các bạn đến với mạng xã hội cựu sinh viên!",
        user: { id: 2, fullName: "Trần Thị B" },
        commentsLocked: false,
        comments: [
            { id: 1, content: "Hay quá!", user: { id: 1, fullName: "Nguyễn Văn A" } },
        ]
    }
];

const Home = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Giả lập dữ liệu
        setPosts(dummyPosts);
    }, []);

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Bảng tin</h2>
            {posts.map(post => (
                <PostItem key={post.id} post={post} currentUser={dummyUser} />
            ))}
        </div>
    );
};

export default Home;
