// src/configs/Apis.js
import axios from "axios";

// âœ… Base URL cá»§a backend
const BASE_URL = "http://localhost:8080/AlumniSocialNetwork/api";

// âœ… Táº¡o Axios instance
const apiClient = axios.create({
  baseURL: BASE_URL,
  headers: {
    "Content-Type": "application/json",
    // Authorization: `Bearer ${token}` náº¿u cáº§n auth
  },
});

// âœ… Endpoint
const endpoints = {
  posts: "/post",
  comments: "/comment",
  reactions: "/reaction",
};

// âœ… API chÃ­nh
const Apis = {
  // ------------------ ðŸ“Œ POST ------------------
  getAllPosts: () => apiClient.get(endpoints.posts),
  getPostById: (id) => apiClient.get(`${endpoints.posts}/${id}`),
  createPost: (data) => apiClient.post(endpoints.posts, data),
  updatePost: (id, data) => apiClient.put(`${endpoints.posts}/${id}`, data),
  deletePost: (id) => apiClient.delete(`${endpoints.posts}/${id}`),

  // âœ… Lock hoáº·c má»Ÿ bÃ¬nh luáº­n
  toggleLockComments: (postId, lock) =>
    apiClient.post(`${endpoints.posts}/${postId}/lock-comments?lock=${lock}`),

  // ------------------ ðŸ’¬ COMMENT ------------------
  getCommentsByPostId: (postId) =>
    apiClient.get(`${endpoints.comments}/post/${postId}`),
  createComment: (data) => apiClient.post(endpoints.comments, data),
  updateComment: (id, data) =>
    apiClient.put(`${endpoints.comments}/${id}`, data),
  deleteComment: (id) => apiClient.delete(`${endpoints.comments}/${id}`),

  // ------------------ â¤ï¸ REACTION ------------------
  reactToPost: (reaction) => apiClient.post(endpoints.reactions, reaction),

  getReactionStatsByPostId: (postId) =>
    apiClient.get(`${endpoints.reactions}/stats/${postId}`),

  getUserReactionForPost: (userId, postId) =>
    apiClient.get(`${endpoints.reactions}/user/${userId}/post/${postId}`),
};

export default Apis;
