import { useEffect, useState } from "react";
import { Alert, Button, Card, Col, Form, Image, Row, Spinner } from "react-bootstrap";
import Apis, { endpoints } from "../configs/Apis";
import MySpinner from "./layout/MySpinner";
import moment from "moment";

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadPosts = async () => {
    try {
      let res = await Apis.get(endpoints["posts"]);
      setPosts(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPosts();
  }, []);

  const reactionEmoji = {
    like: "üëç",
    heart: "‚ù§Ô∏è",
    haha: "üòÇ"
  };

  const PostItem = ({ post }) => {
    const [details, setDetails] = useState(null);
    const [loadingDetail, setLoadingDetail] = useState(false);
    const [commentContent, setCommentContent] = useState("");

    const loadPostDetails = async () => {
      setLoadingDetail(true);
      try {
        let res = await Apis.get(`${endpoints["posts"]}/${post.id}`);
        setDetails(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoadingDetail(false);
      }
    };

    useEffect(() => {
      loadPostDetails();
    }, []);

    const postComment = async () => {
      try {
        const payload = {
          content: commentContent,
          post: { id: post.id }
        };
        await Apis.post(endpoints["comments"], payload);
        loadPostDetails(); // refresh comments
        setCommentContent("");
      } catch (err) {
        console.error(err);
      }
    };

    if (loadingDetail) return <MySpinner />;

    if (!details) return null;

    return (
      <Card className="mb-3 shadow-sm">
        <Card.Body>
          <Row>
            <Col xs={1}>
              <Image roundedCircle src={details.user.avatar || "/avatar.png"} width="40" height="40" />
            </Col>
            <Col>
              <strong>{details.user.name}</strong> <br />
              <small className="text-muted">{moment(details.createdDate).fromNow()}</small>
            </Col>
          </Row>
          <Card.Text className="mt-2">{details.content}</Card.Text>
          {details.image && (
            <div className="text-center mb-2">
              <Image src={details.image} fluid rounded />
            </div>
          )}

          <div>
            {Object.entries(details.reactionStats || {}).map(([type, count]) => (
              <span key={type} className="me-2">
                {reactionEmoji[type]} {count}
              </span>
            ))}
          </div>

          <Form.Group className="mt-2 d-flex">
            <Form.Control
              placeholder="Vi·∫øt b√¨nh lu·∫≠n..."
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
            />
            <Button variant="primary" onClick={postComment} className="ms-2">
              G·ª≠i
            </Button>
          </Form.Group>

          <div className="mt-3">
            <strong>B√¨nh lu·∫≠n:</strong>
            {details.comments?.map((c) => (
              <div key={c.id} className="mt-2">
                <strong>{c.user.name}</strong>: {c.content}
              </div>
            ))}
          </div>
        </Card.Body>
      </Card>
    );
  };

  if (loading) return <MySpinner />;

  return (
    <>
      {posts.length === 0 && <Alert variant="info">Kh√¥ng c√≥ b√†i vi·∫øt n√†o!</Alert>}

      {posts.map((post) => (
        <PostItem key={post.id} post={post} />
      ))}
    </>
  );
};

export default PostList;
